$(document).ready(function(){
	
	// Lift card and show stats on Mouseover
	$('#product-card').hover(function(){
			$(this).addClass('animate');				
		 }, function(){
			$(this).removeClass('animate');			
	});	
	$('#space-register').hover(function(){
		$(this).addClass('animate');				
	 }, function(){
		$(this).removeClass('animate');			
});	
$('#space-login').hover(function(){
	$(this).addClass('animate');				
 }, function(){
	$(this).removeClass('animate');			
});	
$('#content>.inner .components a li').hover(function(){
	$(this).addClass('animate');				
 }, function(){
	$(this).removeClass('animate');			
});	
$(".rightcontent > .listproduct a").hover(
	function (e) {
			$(this).children("ul").fadeIn(150);
			e.preventDefault();
	}, function (e) {
			$(this).children("ul").fadeOut(150);
			e.preventDefault();
	}
);
});